mapper.select:
select nId,mId,title,content,regDate
from (select nId,mId,title,content,regDate,
row_number() over(order by nId desc) as rNum
from notice
where 1=1



) mp
where rNum between #{rowStart} and #{rowEnd}
order by nId desc

mapper.update:
update notice set title =#{title}, content
=#{content}, regDate = sysdate
where nId = #{nId}
